 8export function ThemeToggle() {
 9  const { theme, setTheme } = useTheme();
10
11  const toggleTheme = () => {
12    // Simple toggle between light and dark
13    if (theme === 'dark') {
14      setTheme('light');
15    } else {
16      setTheme('dark');
17    }
18  };
19
20  return (
21    <Button variant="ghost" size="icon" onClick={toggleTheme}>
22      <Sun className="h-[1.2rem] w-[1.2rem] rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
23      <Moon className="absolute h-[1.2rem] w-[1.2rem] rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100" />
24      <span className="sr-only">Toggle theme</span>
25    </Button>
26  );
27}
