 3import { createContext, useContext, useEffect, useState } from 'react';
 4
 5type Theme = 'dark' | 'light' | 'system';
 6
 7type ThemeProviderProps = {
 8  children: React.ReactNode;
 9  defaultTheme?: Theme;
10  storageKey?: string;
11};
12
13type ThemeProviderState = {
14  theme: Theme;
15  setTheme: (theme: Theme) => void;
16};
17
18const initialState: ThemeProviderState = {
19  theme: 'system',
20  setTheme: () => null,
21};
22
23const ThemeProviderContext = createContext<ThemeProviderState>(initialState);
24
25export function ThemeProvider({
26  children,
27  defaultTheme = 'system',
28  storageKey = 'acolyte-theme',
29  ...props
30}: ThemeProviderProps) {
31  const [theme, setTheme] = useState<Theme>(defaultTheme);
32  const [mounted, setMounted] = useState(false);
33
34  // Hydration fix: only access localStorage after component mounts
35  useEffect(() => {
36    setMounted(true);
37    const storedTheme = localStorage?.getItem(storageKey) as Theme;
38    if (storedTheme) {
39      setTheme(storedTheme);
40    }
41  }, [storageKey]);
42
43  useEffect(() => {
44    if (!mounted) return;
45
46    const root = window.document.documentElement;
47    root.classList.remove('light', 'dark');
48
49    if (theme === 'system') {
50      const systemTheme = window.matchMedia('(prefers-color-scheme: dark)')
51        .matches
52        ? 'dark'
53        : 'light';
54
55      root.classList.add(systemTheme);
56      return;
57    }
58
59    root.classList.add(theme);
60  }, [theme, mounted]);
61
62  const value = {
63    theme,
64    setTheme: (theme: Theme) => {
65      if (typeof window !== 'undefined') {
66        localStorage?.setItem(storageKey, theme);
67      }
68      setTheme(theme);
69    },
70  };
71
72  return (
73    <ThemeProviderContext.Provider {...props} value={value}>
74      {children}
75    </ThemeProviderContext.Provider>
76  );
77}
78
79export const useTheme = () => {
80  const context = useContext(ThemeProviderContext);
81
82  if (context === undefined)
83    throw new Error('useTheme must be used within a ThemeProvider');
84
85  return context;
86};
