 21interface TabData {
 22  id: string;
 23  name: string;
 24  url: string;
 25  method: string;
 26  headers: string;
 27  requestBody: string;
 28}
 29
 30export default function BasicAPIsPage() {
 31  const [tabs, setTabs] = useState<TabData[]>([
 32    {
 33      id: '1',
 34      name: 'Request 1',
 35      url: 'https://jsonplaceholder.typicode.com/posts',
 36      method: 'GET',
 37      headers: 'Content-Type: application/json',
 38      requestBody: '',
 39    },
 40  ]);
 41  const [activeTab, setActiveTab] = useState('1');
 42  const [editingTabId, setEditingTabId] = useState<string | null>(null);
 43  const [editingTabName, setEditingTabName] = useState<string>('');
 44  const editInputRef = useRef<HTMLInputElement>(null);
 45
 46  // Project management state
 47  const [savedProjects, setSavedProjects] = useState<APIProject[]>([]);
 48  const [currentProject, setCurrentProject] = useState<APIProject | null>(null);
 49  const [saveDialogOpen, setSaveDialogOpen] = useState(false);
 50  const [loadDialogOpen, setLoadDialogOpen] = useState(false);
 51  const [newProjectName, setNewProjectName] = useState('');
 52  const [newProjectDescription, setNewProjectDescription] = useState('');
 53
 54  const addNewTab = () => {
 55    const newId = Date.now().toString(); // Use timestamp for unique ID
 56    const newTab: TabData = {
 57      id: newId,
 58      name: `Request ${tabs.length + 1}`,
 59      url: 'https://jsonplaceholder.typicode.com/posts',
 60      method: 'GET',
 61      headers: 'Content-Type: application/json',
 62      requestBody: '',
 63    };
 64    setTabs((prevTabs) => [...prevTabs, newTab]);
 65    setActiveTab(newId);
 66  };
 67
 68  const removeTab = (id: string) => {
 69    setTabs((prevTabs) => {
 70      if (prevTabs.length <= 1) return prevTabs; // Don't allow removing the last tab
 71
 72      const newTabs = prevTabs.filter((tab) => tab.id !== id);
 73
 74      // If we're removing the active tab, switch to the first tab
 75      setActiveTab((prevActiveTab) =>
 76        prevActiveTab === id ? newTabs[0].id : prevActiveTab,
 77      );
 78
 79      return newTabs;
 80    });
 81  };
 82
 83  const updateTabData = (id: string, updates: Partial<Omit<TabData, 'id'>>) => {
 84    setTabs((prevTabs) => {
 85      const newTabs = prevTabs.map((tab) =>
 86        tab.id === id ? { ...tab, ...updates } : tab,
 87      );
 88      return newTabs;
 89    });
 90  };
 91
 92  const startEditingTab = (id: string, currentName: string) => {
 93    setEditingTabId(id);
 94    setEditingTabName(currentName);
 95  };
 96
 97  const saveTabName = (id: string) => {
 98    if (editingTabName.trim()) {
 99      updateTabData(id, { name: editingTabName.trim() });
100    }
101    setEditingTabId(null);
102    setEditingTabName('');
103  };
104
105  const cancelEditingTab = () => {
106    setEditingTabId(null);
107    setEditingTabName('');
108  };
109
110  // Focus the input when editing starts
111  useEffect(() => {
112    if (editingTabId && editInputRef.current) {
113      editInputRef.current.focus();
114      editInputRef.current.select();
115    }
116  }, [editingTabId]);
117
118  // Load saved projects and current project on component mount
119  useEffect(() => {
120    const loadProjectsAndCurrent = async () => {
121      try {
122        // Load all projects
123        const projects = await apiProjectsStorage.list();
124        setSavedProjects(projects);
125
126        // Load current project if it exists
127        const current = await apiProjectsStorage.getCurrentProject();
128        if (current) {
129          setCurrentProject(current);
130          setTabs(current.tabs);
131          setActiveTab(current.tabs[0]?.id || '1');
132        }
133      } catch (error) {
134        console.warn('Failed to load projects:', error);
135        toast.error('Failed to load saved projects');
136      }
137    };
138    loadProjectsAndCurrent();
139  }, []);
140
141  // Project management functions
142  const saveCurrentProject = async () => {
143    // If we have a current project, update it
144    if (currentProject) {
145      try {
146        const updatedProject = apiProjectsStorage.updateProject(
147          currentProject,
148          tabs,
149        );
150
151        await apiProjectsStorage.save(updatedProject);
152        setCurrentProject(updatedProject);
153
154        // Update the projects list
155        const updatedProjects = await apiProjectsStorage.list();
156        setSavedProjects(updatedProjects);
157
158        toast.success(`Project "${updatedProject.name}" updated successfully!`);
159      } catch (error) {
160        console.error('Failed to update project:', error);
161        toast.error('Failed to update project');
162      }
163      return;
164    }
165
166    // Otherwise, create a new project
167    if (!newProjectName.trim()) {
168      toast.error('Please enter a project name');
169      return;
170    }
171
172    try {
173      const project = apiProjectsStorage.createProject(
174        newProjectName.trim(),
175        tabs,
176        newProjectDescription.trim() || undefined,
177      );
178
179      await apiProjectsStorage.save(project);
180      await apiProjectsStorage.setCurrentProjectId(project.id);
181      setCurrentProject(project);
182
183      // Update the projects list
184      const updatedProjects = await apiProjectsStorage.list();
185      setSavedProjects(updatedProjects);
186
187      // Reset dialog state
188      setNewProjectName('');
189      setNewProjectDescription('');
190      setSaveDialogOpen(false);
191
192      toast.success(`Project "${project.name}" saved successfully!`);
193    } catch (error) {
194      console.error('Failed to save project:', error);
195      toast.error('Failed to save project');
196    }
197  };
198
199  const saveAsNewProject = async () => {
200    if (!newProjectName.trim()) {
201      toast.error('Please enter a project name');
202      return;
203    }
204
205    try {
206      const project = apiProjectsStorage.createProject(
207        newProjectName.trim(),
208        tabs,
209        newProjectDescription.trim() || undefined,
210      );
211
212      await apiProjectsStorage.save(project);
213      await apiProjectsStorage.setCurrentProjectId(project.id);
214      setCurrentProject(project);
215
216      // Update the projects list
217      const updatedProjects = await apiProjectsStorage.list();
218      setSavedProjects(updatedProjects);
219
220      // Reset dialog state
221      setNewProjectName('');
222      setNewProjectDescription('');
223      setSaveDialogOpen(false);
224
225      toast.success(`Project "${project.name}" saved successfully!`);
226    } catch (error) {
227      console.error('Failed to save project:', error);
228      toast.error('Failed to save project');
229    }
230  };
231
232  const loadProject = async (projectId: string) => {
233    try {
234      const project = await apiProjectsStorage.load(projectId);
235      if (!project) {
236        toast.error('Project not found');
237        return;
238      }
239
240      // Replace current tabs with project tabs
241      setTabs(project.tabs);
242      setActiveTab(project.tabs[0]?.id || '1');
243      setCurrentProject(project);
244      await apiProjectsStorage.setCurrentProjectId(project.id);
245      setLoadDialogOpen(false);
246
247      toast.success(`Project "${project.name}" loaded successfully!`);
248    } catch (error) {
249      console.error('Failed to load project:', error);
250      toast.error('Failed to load project');
251    }
252  };
253
254  const clearCurrentProject = async () => {
255    try {
256      await apiProjectsStorage.setCurrentProjectId(null);
257      setCurrentProject(null);
258      toast.success('Current project cleared');
259    } catch (error) {
260      console.error('Failed to clear current project:', error);
261      toast.error('Failed to clear current project');
262    }
263  };
264
265  const deleteProject = async (projectId: string) => {
266    try {
267      const project = savedProjects.find((p) => p.id === projectId);
268      await apiProjectsStorage.delete(projectId);
269
270      // If this was the current project, clear it
271      if (currentProject?.id === projectId) {
272        setCurrentProject(null);
273      }
274
275      // Update the projects list
276      const updatedProjects = await apiProjectsStorage.list();
277      setSavedProjects(updatedProjects);
278
279      toast.success(`Project "${project?.name}" deleted successfully!`);
280    } catch (error) {
281      console.error('Failed to delete project:', error);
282      toast.error('Failed to delete project');
283    }
284  };
285
286  return (
287    <div className="flex flex-1 flex-col gap-4">
288      <div className="flex items-center justify-between">
289        <div>
290          <h1 className="text-3xl font-bold">APIs</h1>
291          {currentProject && (
292            <p className="text-sm text-muted-foreground mt-1">
293              Current project:{' '}
294              <span className="font-medium">{currentProject.name}</span>
295              {currentProject.description && (
296                <span className="ml-2"> {currentProject.description}</span>
297              )}
298            </p>
299          )}
300        </div>
301
302        {/* Project Management Controls */}
303        <div className="flex items-center gap-2">
304          {/* Quick Save Button (only show if current project exists) */}
305          {currentProject && (
306            <Button variant="default" size="sm" onClick={saveCurrentProject}>
307              <Save className="h-4 w-4 mr-2" />
308              Save
309            </Button>
310          )}
311
312          {/* Save Project Dialog */}
313          <Dialog open={saveDialogOpen} onOpenChange={setSaveDialogOpen}>
314            <DialogTrigger asChild>
315              <Button variant="outline" size="sm">
316                <Save className="h-4 w-4 mr-2" />
317                {currentProject ? 'Save As New' : 'Save Project'}
318              </Button>
319            </DialogTrigger>
320            <DialogContent>
321              <DialogHeader>
322                <DialogTitle>
323                  {currentProject ? 'Save As New Project' : 'Save API Project'}
324                </DialogTitle>
325                <DialogDescription>
326                  {currentProject
327                    ? 'Save your current API configuration as a new project.'
328                    : 'Save your current API configuration as a project for later use.'}
329                </DialogDescription>
330              </DialogHeader>
331              <div className="space-y-4">
332                <div>
333                  <label
334                    htmlFor="project-name"
335                    className="text-sm font-medium mb-2 block"
336                  >
337                    Project Name *
338                  </label>
339                  <Input
340                    id="project-name"
341                    value={newProjectName}
342                    onChange={(e) => setNewProjectName(e.target.value)}
343                    placeholder="Enter project name..."
344                  />
345                </div>
346                <div>
347                  <label
348                    htmlFor="project-description"
349                    className="text-sm font-medium mb-2 block"
350                  >
351                    Description (optional)
352                  </label>
353                  <Input
354                    id="project-description"
355                    value={newProjectDescription}
356                    onChange={(e) => setNewProjectDescription(e.target.value)}
357                    placeholder="Enter project description..."
358                  />
359                </div>
360              </div>
361              <DialogFooter>
362                <Button
363                  variant="outline"
364                  onClick={() => setSaveDialogOpen(false)}
365                >
366                  Cancel
367                </Button>
368                <Button
369                  onClick={
370                    currentProject ? saveAsNewProject : saveCurrentProject
371                  }
372                >
373                  Save Project
374                </Button>
375              </DialogFooter>
376            </DialogContent>
377          </Dialog>
378
379          {/* Load Project Dialog */}
380          <Dialog open={loadDialogOpen} onOpenChange={setLoadDialogOpen}>
381            <DialogTrigger asChild>
382              <Button variant="outline" size="sm">
383                <FolderOpen className="h-4 w-4 mr-2" />
384                Load Project
385              </Button>
386            </DialogTrigger>
387            <DialogContent>
388              <DialogHeader>
389                <DialogTitle>Load API Project</DialogTitle>
390                <DialogDescription>
391                  Select a saved project to load. This will replace your current
392                  tabs.
393                </DialogDescription>
394              </DialogHeader>
395              <div className="space-y-4">
396                {savedProjects.length === 0 ? (
397                  <p className="text-sm text-muted-foreground text-center py-4">
398                    No saved projects found. Save your current configuration as
399                    a project first.
400                  </p>
401                ) : (
402                  <div className="space-y-2 max-h-96 overflow-y-auto">
403                    {savedProjects.map((project) => (
404                      <div
405                        key={project.id}
406                        className="flex items-center justify-between p-3 border rounded-md hover:bg-muted/50"
407                      >
408                        <div className="flex-1">
409                          <h4 className="font-medium flex items-center gap-2">
410                            {project.name}
411                            {currentProject?.id === project.id && (
412                              <span className="text-xs bg-primary text-primary-foreground px-2 py-1 rounded">
413                                Current
414                              </span>
415                            )}
416                          </h4>
417                          {project.description && (
418                            <p className="text-sm text-muted-foreground">
419                              {project.description}
420                            </p>
421                          )}
422                          <p className="text-xs text-muted-foreground">
423                            {project.tabs.length} tabs  Saved{' '}
424                            {new Date(project.savedAt).toLocaleDateString()}
425                          </p>
426                        </div>
