461            <Button
462              variant="outline"
463              size="sm"
464              onClick={clearCurrentProject}
465              title="Clear current project"
466            >
467              <FileX className="h-4 w-4" />
468            </Button>
469          )}
470        </div>
471      </div>
472
473      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
474        <div className="flex items-center gap-2 mb-4">
475          <TabsList>
476            {tabs.map((tab) => (
477              <TabsTrigger
478                key={tab.id}
479                value={tab.id}
480                className="relative group"
481              >
482                {editingTabId === tab.id ? (
483                  <Input
484                    ref={editInputRef}
485                    value={editingTabName}
486                    onChange={(e) => setEditingTabName(e.target.value)}
487                    onKeyDown={(e) => {
488                      e.stopPropagation();
489                      if (e.key === 'Enter') {
490                        saveTabName(tab.id);
491                      } else if (e.key === 'Escape') {
492                        cancelEditingTab();
493                      }
494                    }}
495                    onBlur={() => saveTabName(tab.id)}
496                    className="h-6 text-xs border-none p-1 min-w-[80px] bg-transparent"
497                    onClick={(e) => e.stopPropagation()}
498                  />
499                ) : (
500                  <span
501                    className="mr-2 cursor-pointer"
502                    onDoubleClick={(e) => {
503                      e.stopPropagation();
504                      startEditingTab(tab.id, tab.name);
505                    }}
506                    title="Double-click to edit name"
507                  >
508                    {tab.name}
509                  </span>
510                )}
511                {tabs.length > 1 && (
512                  <div
513                    onClick={(e) => {
514                      e.stopPropagation();
515                      removeTab(tab.id);
516                    }}
517                    className="ml-1 opacity-0 group-hover:opacity-100 hover:bg-destructive/20 rounded-sm p-0.5 transition-opacity cursor-pointer"
518                    title="Close tab"
519                    role="button"
520                    tabIndex={0}
521                    onKeyDown={(e) => {
522                      if (e.key === 'Enter' || e.key === ' ') {
523                        e.preventDefault();
524                        e.stopPropagation();
525                        removeTab(tab.id);
526                      }
527                    }}
528                  >
529                    <X className="h-3 w-3" />
530                  </div>
531                )}
532              </TabsTrigger>
533            ))}
534          </TabsList>
535          <Button
536            variant="outline"
537            size="sm"
538            onClick={addNewTab}
539            className="flex items-center gap-1"
540          >
541            <Plus className="h-4 w-4" />
542            Add Tab
543          </Button>
544        </div>
545
546        {tabs.map((tab) => (
547          <TabsContent key={tab.id} value={tab.id}>
548            <ApiRequestForm
549              url={tab.url}
550              method={tab.method}
551              headers={tab.headers}
552              requestBody={tab.requestBody}
553              onUrlChange={(value) => updateTabData(tab.id, { url: value })}
554              onMethodChange={(value) =>
555                updateTabData(tab.id, { method: value })
556              }
557              onHeadersChange={(value) =>
558                updateTabData(tab.id, { headers: value })
559              }
560              onRequestBodyChange={(value) =>
561                updateTabData(tab.id, { requestBody: value })
562              }
563            />
564          </TabsContent>
565        ))}
566      </Tabs>
567    </div>
568  );
569}
