  1'use client';
  2
  3import { Badge } from '@/components/ui/badge';
  4import { Button } from '@/components/ui/button';
  5import {
  6  Card,
  7  CardContent,
  8  CardDescription,
  9  CardHeader,
 10  CardTitle,
 11} from '@/components/ui/card';
 12import { Input } from '@/components/ui/input';
 13import {
 14  Select,
 15  SelectContent,
 16  SelectItem,
 17  SelectTrigger,
 18  SelectValue,
 19} from '@/components/ui/select';
 20import { Textarea } from '@/components/ui/textarea';
 21import { useEffect, useState } from 'react';
 22import { toast } from 'sonner';
 23
 24interface Match {
 25  match: string;
 26  index: number;
 27  groups: string[];
 28}
 29
 30export default function RegexPage() {
 31  const [pattern, setPattern] = useState('');
 32  const [flags, setFlags] = useState('g');
 33  const [testString, setTestString] = useState('');
 34  const [replacement, setReplacement] = useState('');
 35  const [matches, setMatches] = useState<Match[]>([]);
 36  const [replacedText, setReplacedText] = useState('');
 37  const [error, setError] = useState('');
 38  const [isValid, setIsValid] = useState(false);
 39  const [isFullMatch, setIsFullMatch] = useState(false);
 40
 41  // Common regex patterns for quick selection
 42  const commonPatterns = [
 43    {
 44      name: 'Email',
 45      pattern: '^[\\w\\.-]+@[\\w\\.-]+\\.[a-zA-Z]{2,}$',
 46      flags: 'i',
 47    },
 48    {
 49      name: 'URL',
 50      pattern:
 51        'https?:\\/\\/(www\\.)?[-a-zA-Z0-9@:%._\\+~#=]{1,256}\\.[a-zA-Z0-9()]{1,6}\\b([-a-zA-Z0-9()@:%_\\+.~#?&//=]*)',
 52      flags: 'gi',
 53    },
 54    {
 55      name: 'Phone (US)',
 56      pattern: '\\(?([0-9]{3})\\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})',
 57      flags: 'g',
 58    },
 59    {
 60      name: 'Date (MM/DD/YYYY)',
 61      pattern: '^(0[1-9]|1[0-2])\\/(0[1-9]|[12]\\d|3[01])\\/(19|20)\\d{2}$',
 62      flags: '',
 63    },
 64    {
 65      name: 'IP Address',
 66      pattern:
 67        '^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$',
 68      flags: '',
 69    },
 70    {
 71      name: 'Hex Color',
 72      pattern: '^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$',
 73      flags: 'i',
 74    },
 75    {
 76      name: 'Credit Card',
 77      pattern:
 78        '^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13}|3[0-9]{13}|6(?:011|5[0-9]{2})[0-9]{12})$',
 79      flags: '',
 80    },
 81    { name: 'HTML Tags', pattern: '<\\/?[a-z][\\s\\S]*>', flags: 'gi' },
 82    { name: 'Whitespace', pattern: '\\s+', flags: 'g' },
 83    { name: 'Numbers Only', pattern: '^[0-9]+$', flags: '' },
 84  ];
 85
 86  // Test regex and find matches
 87  useEffect(() => {
 88    if (!pattern || !testString) {
 89      setMatches([]);
 90      setError('');
 91      setIsValid(false);
 92      setIsFullMatch(false);
 93      return;
 94    }
 95
 96    try {
 97      const regex = new RegExp(pattern, flags);
 98      setIsValid(true);
 99      setError('');
100
101      // Check if the entire string matches the pattern (validation mode)
102      const fullMatchRegex = new RegExp(
103        `^(?:${pattern})$`,
104        flags.replace('g', ''),
105      );
106      const fullMatch = fullMatchRegex.test(testString);
107      setIsFullMatch(fullMatch);
108
109      const foundMatches: Match[] = [];
110      let match;
111
112      if (flags.includes('g')) {
113        while ((match = regex.exec(testString)) !== null) {
114          foundMatches.push({
115            match: match[0],
116            index: match.index,
117            groups: match.slice(1),
118          });
119          // Prevent infinite loops with zero-length matches
120          if (match[0].length === 0) {
121            regex.lastIndex++;
122          }
123        }
124      } else {
125        match = regex.exec(testString);
126        if (match) {
127          foundMatches.push({
128            match: match[0],
129            index: match.index,
130            groups: match.slice(1),
131          });
132        }
133      }
134
135      setMatches(foundMatches);
136    } catch (err) {
137      setError((err as Error).message);
138      setIsValid(false);
139      setMatches([]);
140      setIsFullMatch(false);
141    }
142  }, [pattern, flags, testString]);
143
144  // Update replaced text
145  useEffect(() => {
146    if (!pattern || !testString || !replacement) {
147      setReplacedText('');
148      return;
149    }
150
151    try {
152      const regex = new RegExp(pattern, flags);
153      const result = testString.replace(regex, replacement);
154      setReplacedText(result);
155    } catch {
156      setReplacedText('');
157    }
158  }, [pattern, flags, testString, replacement]);
159
160  const copyToClipboard = async (text: string, label: string) => {
161    try {
162      await navigator.clipboard.writeText(text);
163      toast.success(`${label} copied to clipboard!`);
164    } catch {
165      toast.error('Failed to copy to clipboard');
166    }
167  };
168
169  const loadCommonPattern = (patternObj: (typeof commonPatterns)[0]) => {
170    setPattern(patternObj.pattern);
171    setFlags(patternObj.flags);
172    toast.success(`Loaded ${patternObj.name} pattern`);
173  };
174
175  const clearAll = () => {
176    setPattern('');
177    setFlags('g');
178    setTestString('');
179    setReplacement('');
180    setMatches([]);
181    setReplacedText('');
182    setError('');
183    setIsValid(false);
184    toast.success('All fields cleared!');
185  };
186
187  const highlightMatches = (text: string, matches: Match[]) => {
188    if (matches.length === 0) return text;
189
190    let highlightedText = text;
191    let offset = 0;
192
193    matches.forEach((match, index) => {
194      const startTag = `<mark class="bg-yellow-200 dark:bg-yellow-800" data-match="${index}">`;
195      const endTag = '</mark>';
196      const insertPos = match.index + offset;
197
198      highlightedText =
199        highlightedText.slice(0, insertPos) +
200        startTag +
201        match.match +
202        endTag +
203        highlightedText.slice(insertPos + match.match.length);
204
205      offset += startTag.length + endTag.length;
206    });
207
208    return highlightedText;
209  };
210
211  return (
212    <div className="flex flex-1 flex-col gap-4">
213      <div className="flex items-center justify-between">
214        <h1 className="text-3xl font-bold">Regex Tester</h1>
215        <Button onClick={clearAll} variant="outline">
216          Clear All
217        </Button>
218      </div>
219
220      <div className="grid gap-4 md:grid-cols-2">
221        <Card>
222          <CardHeader>
223            <CardTitle>Regular Expression</CardTitle>
224            <CardDescription>
225              Enter your regex pattern and flags
226            </CardDescription>
227          </CardHeader>
228          <CardContent className="space-y-4">
229            <div>
230              <label className="text-sm font-medium mb-2 block">Pattern</label>
231              <div className="flex items-center gap-2">
232                <span className="text-lg font-mono">/</span>
233                <Input
234                  value={pattern}
235                  onChange={(e) => setPattern(e.target.value)}
236                  placeholder="Enter regex pattern..."
237                  className="flex-1 font-mono"
238                />
239                <span className="text-lg font-mono">/</span>
240                <Input
241                  value={flags}
242                  onChange={(e) => setFlags(e.target.value)}
243                  placeholder="flags"
244                  className="w-20 font-mono"
245                />
246              </div>
247              {error && (
248                <p className="text-sm text-destructive mt-2">{error}</p>
249              )}
250              {isValid && pattern && (
251                <div className="flex items-center gap-2 mt-2">
252                  <Badge variant="default" className="bg-green-500">
253                    Valid Regex
254                  </Badge>
255                  <Button
256                    size="sm"
257                    variant="outline"
258                    onClick={() =>
259                      copyToClipboard(`/${pattern}/${flags}`, 'Regex')
260                    }
261                  >
262                    Copy Regex
263                  </Button>
264                </div>
265              )}
266            </div>
267
268            <div>
269              <label className="text-sm font-medium mb-2 block">
270                Common Patterns
271              </label>
272              <Select
273                onValueChange={(value) => {
274                  const selected = commonPatterns.find((p) => p.name === value);
275                  if (selected) loadCommonPattern(selected);
276                }}
277              >
278                <SelectTrigger>
279                  <SelectValue placeholder="Select a common pattern..." />
280                </SelectTrigger>
281                <SelectContent>
282                  {commonPatterns.map((pattern) => (
283                    <SelectItem key={pattern.name} value={pattern.name}>
284                      {pattern.name}
285                    </SelectItem>
286                  ))}
287                </SelectContent>
288              </Select>
289            </div>
290
291            <div>
292              <label className="text-sm font-medium mb-2 block">
293                Flags Guide
294              </label>
295              <div className="text-xs space-y-1 text-muted-foreground">
296                <div>
297                  <code>g</code> - Global (find all matches)
298                </div>
299                <div>
300                  <code>i</code> - Case insensitive
301                </div>
302                <div>
303                  <code>m</code> - Multiline
304                </div>
305                <div>
306                  <code>s</code> - Dotall (. matches newline)
307                </div>
308                <div>
309                  <code>u</code> - Unicode
310                </div>
311                <div>
312                  <code>y</code> - Sticky
313                </div>
314              </div>
315            </div>
316          </CardContent>
317        </Card>
318
319        <Card>
320          <CardHeader>
321            <div className="flex items-center justify-between">
322              <div>
323                <CardTitle>Test String</CardTitle>
324                <CardDescription>
325                  Enter text to test your regex against. The badge shows if the
326                  entire string matches the pattern.
327                </CardDescription>
328              </div>
329              {pattern && testString && (
330                <Badge
331                  variant={isFullMatch ? 'default' : 'secondary'}
332                  className={
333                    isFullMatch
334                      ? 'bg-green-500 hover:bg-green-600'
335                      : 'bg-red-500 hover:bg-red-600 text-white'
336                  }
337                >
338                  {isFullMatch ? ' Valid' : ' Invalid'}
339                </Badge>
340              )}
341            </div>
342          </CardHeader>
343          <CardContent>
344            <Textarea
345              value={testString}
346              onChange={(e) => setTestString(e.target.value)}
347              placeholder="Enter test string here..."
348              className="min-h-[200px] font-mono text-sm"
349            />
350            <div className="flex items-center justify-between mt-2">
351              <span className="text-sm text-muted-foreground">
352                {testString.length} characters
353              </span>
354              <Button
355                size="sm"
356                variant="outline"
357                onClick={() => copyToClipboard(testString, 'Test string')}
358                disabled={!testString}
359              >
360                Copy Text
361              </Button>
362            </div>
363          </CardContent>
364        </Card>
365      </div>
366
367      {/* Matches Section */}
368      {matches.length > 0 && (
369        <Card>
370          <CardHeader>
371            <CardTitle className="flex items-center gap-2">
372              Matches
373              <Badge variant="secondary">
374                {matches.length} {matches.length === 1 ? 'match' : 'matches'}
375              </Badge>
