470          </div>
471
472          {replacedText && (
473            <div>
474              <div className="flex items-center justify-between mb-2">
475                <label className="text-sm font-medium">Result</label>
476                <Button
477                  size="sm"
478                  variant="outline"
479                  onClick={() => copyToClipboard(replacedText, 'Replaced text')}
480                >
481                  Copy Result
482                </Button>
483              </div>
484              <Textarea
485                value={replacedText}
486                readOnly
487                className="min-h-[100px] font-mono text-sm bg-muted/50"
488              />
489            </div>
490          )}
491        </CardContent>
492      </Card>
493    </div>
494  );
495}
