  3import { Button } from '@/components/ui/button';
  4import {
  5  Card,
  6  CardContent,
  7  CardDescription,
  8  CardHeader,
  9  CardTitle,
 10} from '@/components/ui/card';
 11import {
 12  Select,
 13  SelectContent,
 14  SelectItem,
 15  SelectTrigger,
 16  SelectValue,
 17} from '@/components/ui/select';
 18import { useState } from 'react';
 19import { toast } from 'sonner';
 20
 21export default function JsonFormatterPage() {
 22  const [inputJson, setInputJson] = useState('');
 23  const [output, setOutput] = useState('');
 24  const [indentSize, setIndentSize] = useState('2');
 25  const [sortKeys, setSortKeys] = useState(false);
 26  const [error, setError] = useState('');
 27
 28  const formatJson = () => {
 29    try {
 30      setError('');
 31      const parsed = JSON.parse(inputJson);
 32      const formatted = JSON.stringify(
 33        sortKeys ? sortObjectKeys(parsed) : parsed,
 34        null,
 35        parseInt(indentSize),
 36      );
 37      setOutput(formatted);
 38      toast.success('JSON formatted successfully!');
 39    } catch (err) {
 40      setError('Invalid JSON: ' + (err as Error).message);
 41      setOutput('');
 42      toast.error('Invalid JSON format');
 43    }
 44  };
 45
 46  const minifyJson = () => {
 47    try {
 48      setError('');
 49      const parsed = JSON.parse(inputJson);
 50      const minified = JSON.stringify(parsed);
 51      setOutput(minified);
 52      toast.success('JSON minified successfully!');
 53    } catch (err) {
 54      setError('Invalid JSON: ' + (err as Error).message);
 55      setOutput('');
 56      toast.error('Invalid JSON format');
 57    }
 58  };
 59
 60  const validateJson = () => {
 61    try {
 62      JSON.parse(inputJson);
 63      setError('');
 64      toast.success('Valid JSON!');
 65    } catch (err) {
 66      setError('Invalid JSON: ' + (err as Error).message);
 67      toast.error('Invalid JSON format');
 68    }
 69  };
 70
 71  const sortObjectKeys = (obj: unknown): unknown => {
 72    if (Array.isArray(obj)) {
 73      return obj.map(sortObjectKeys);
 74    } else if (obj !== null && typeof obj === 'object') {
 75      const sortedObj: Record<string, unknown> = {};
 76      Object.keys(obj as Record<string, unknown>)
 77        .sort()
 78        .forEach((key) => {
 79          sortedObj[key] = sortObjectKeys(
 80            (obj as Record<string, unknown>)[key],
 81          );
 82        });
 83      return sortedObj;
 84    }
 85    return obj;
 86  };
 87
 88  const copyToClipboard = async (text: string) => {
 89    try {
 90      await navigator.clipboard.writeText(text);
 91      toast.success('Copied to clipboard!');
 92    } catch {
 93      toast.error('Failed to copy to clipboard');
 94    }
 95  };
 96
 97  const clearAll = () => {
 98    setInputJson('');
 99    setOutput('');
100    setError('');
101    toast.info('Cleared all fields');
102  };
103
104  return (
105    <div className="flex flex-1 flex-col gap-4">
106      <div className="flex items-center justify-between">
