108      </div>
109
110      <div className="grid gap-4 md:grid-cols-2">
111        <Card>
112          <CardHeader>
113            <CardTitle>Input JSON</CardTitle>
114            <CardDescription>
115              Paste your JSON here to format, minify, or validate
116            </CardDescription>
117          </CardHeader>
118          <CardContent className="space-y-4">
119            <textarea
120              placeholder="Paste your JSON here..."
121              value={inputJson}
122              onChange={(e) => setInputJson(e.target.value)}
123              className="w-full h-96 p-3 text-sm font-mono bg-background border rounded-md resize-none focus:outline-none focus:ring-2 focus:ring-ring"
124            />
125
126            <div className="flex flex-wrap gap-2">
127              <div className="flex items-center gap-2">
128                <label className="text-sm font-medium">Indent:</label>
129                <Select value={indentSize} onValueChange={setIndentSize}>
130                  <SelectTrigger className="w-20">
131                    <SelectValue />
132                  </SelectTrigger>
133                  <SelectContent>
134                    <SelectItem value="2">2</SelectItem>
135                    <SelectItem value="4">4</SelectItem>
136                    <SelectItem value="8">8</SelectItem>
137                  </SelectContent>
138                </Select>
139              </div>
140
141              <div className="flex items-center gap-2">
142                <input
143                  type="checkbox"
144                  id="sortKeys"
145                  checked={sortKeys}
146                  onChange={(e) => setSortKeys(e.target.checked)}
147                  className="rounded"
148                />
149                <label htmlFor="sortKeys" className="text-sm font-medium">
150                  Sort keys
151                </label>
152              </div>
153            </div>
154
155            <div className="flex flex-wrap gap-2">
156              <Button onClick={formatJson} className="flex-1 sm:flex-none">
157                Format
158              </Button>
159              <Button
160                onClick={minifyJson}
161                variant="outline"
162                className="flex-1 sm:flex-none"
163              >
164                Minify
