 17export default function NotepadPage() {
 18  const [content, setContent] = useState('');
 19  const [lastSaved, setLastSaved] = useState<Date | null>(null);
 20  const [isAutoSaving, setIsAutoSaving] = useState(false);
 21  const fileInputRef = useRef<HTMLInputElement>(null);
 22
 23  // Load content from IndexedDB on component mount
 24  useEffect(() => {
 25    const loadContent = async () => {
 26      try {
 27        // First try to migrate from localStorage if needed
 28        await notepadStorage.migrateFromLocalStorage();
 29
 30        // Load from IndexedDB
 31        const savedData = await notepadStorage.load();
 32        if (savedData) {
 33          setContent(savedData.content || '');
 34          setLastSaved(
 35            savedData.lastSaved ? new Date(savedData.lastSaved) : null,
 36          );
 37        }
 38      } catch (error) {
 39        console.warn('Failed to load notepad content:', error);
 40        toast.error('Failed to load saved notes');
 41      }
 42    };
 43
 44    loadContent();
 45  }, []);
 46
 47  // Auto-save to IndexedDB with debouncing
 48  useEffect(() => {
 49    const timeoutId = setTimeout(async () => {
 50      if (!content.trim()) return;
 51
 52      setIsAutoSaving(true);
 53      try {
 54        const saveData = {
 55          content,
 56          lastSaved: new Date().toISOString(),
 57        };
 58
 59        await notepadStorage.save(saveData);
 60        setLastSaved(new Date());
 61      } catch (error) {
 62        console.warn('Auto-save failed:', error);
 63      } finally {
 64        setIsAutoSaving(false);
 65      }
 66    }, 1000); // Auto-save after 1 second of inactivity
 67
 68    return () => clearTimeout(timeoutId);
 69  }, [content]);
 70
 71  // Save content to IndexedDB
 72  const saveToIndexedDB = async () => {
 73    if (!content.trim()) return;
 74
 75    setIsAutoSaving(true);
 76    try {
 77      const saveData = {
 78        content,
 79        lastSaved: new Date().toISOString(),
 80      };
 81
 82      await notepadStorage.save(saveData);
 83      setLastSaved(new Date());
 84    } catch (error) {
 85      console.warn('Save failed:', error);
 86      toast.error('Failed to save notes');
 87    } finally {
 88      setIsAutoSaving(false);
 89    }
 90  };
 91
 92  // Manual save
 93  const handleManualSave = async () => {
 94    await saveToIndexedDB();
 95    toast.success('Notes saved successfully!');
 96  };
 97
 98  // Clear all content
 99  const handleClear = async () => {
100    if (
101      content.trim() &&
102      !confirm(
103        'Are you sure you want to clear all notes? This action cannot be undone.',
104      )
105    ) {
106      return;
107    }
108
109    setContent('');
110    setLastSaved(null);
111    try {
112      await notepadStorage.clear();
113      toast.success('Notes cleared!');
114    } catch (error) {
115      console.warn('Failed to clear notes:', error);
116      toast.error('Failed to clear notes');
117    }
118  };
119
120  // Export notes to markdown file
121  const handleExport = () => {
122    if (!content.trim()) {
123      toast.error('No content to export!');
124      return;
125    }
126
127    const blob = new Blob([content], { type: 'text/markdown' });
128    const url = URL.createObjectURL(blob);
129    const link = document.createElement('a');
130    link.href = url;
131    link.download = `notes-${new Date().toISOString().split('T')[0]}.md`;
132    document.body.appendChild(link);
133    link.click();
134    document.body.removeChild(link);
135    URL.revokeObjectURL(url);
136
137    toast.success('Notes exported successfully!');
138  };
139
140  // Import notes from markdown file
141  const handleImport = (event: React.ChangeEvent<HTMLInputElement>) => {
142    const file = event.target.files?.[0];
143    if (!file) return;
144
145    // Check file type
146    if (
147      !file.name.endsWith('.md') &&
148      !file.name.endsWith('.txt') &&
149      file.type !== 'text/markdown' &&
150      file.type !== 'text/plain'
151    ) {
152      toast.error('Please select a markdown (.md) or text (.txt) file!');
153      return;
154    }
155
156    const reader = new FileReader();
157    reader.onload = (e) => {
158      const fileContent = e.target?.result as string;
159      if (fileContent) {
160        if (
161          content.trim() &&
162          !confirm('Importing will replace your current notes. Continue?')
163        ) {
164          return;
165        }
166        setContent(fileContent);
167        toast.success('Notes imported successfully!');
168      }
169    };
170    reader.onerror = () => {
171      toast.error('Failed to read file!');
172    };
173    reader.readAsText(file);
174
175    // Reset file input
176    if (fileInputRef.current) {
177      fileInputRef.current.value = '';
178    }
179  };
180
181  // Copy all content to clipboard
182  const handleCopyAll = async () => {
183    if (!content.trim()) {
184      toast.error('No content to copy!');
185      return;
186    }
187
188    try {
189      await navigator.clipboard.writeText(content);
190      toast.success('All content copied to clipboard!');
191    } catch {
192      toast.error('Failed to copy to clipboard!');
193    }
194  };
195
196  // Format last saved time
197  const formatLastSaved = (date: Date | null) => {
198    if (!date) return 'Never';
199
200    const now = new Date();
201    const diffMs = now.getTime() - date.getTime();
202    const diffMins = Math.floor(diffMs / 60000);
203
204    if (diffMins < 1) return 'Just now';
205    if (diffMins < 60)
206      return `${diffMins} minute${diffMins === 1 ? '' : 's'} ago`;
207
208    const diffHours = Math.floor(diffMins / 60);
209    if (diffHours < 24)
210      return `${diffHours} hour${diffHours === 1 ? '' : 's'} ago`;
211
212    return date.toLocaleDateString();
213  };
214
215  return (
216    <div className="flex flex-1 flex-col gap-4">
217      <div className="flex items-center justify-between">
218        <h1 className="text-3xl font-bold">Notepad</h1>
219        <div className="flex items-center gap-2 text-sm text-muted-foreground">
220          {isAutoSaving && <span>Saving...</span>}
221          {!isAutoSaving && (
222            <span>Last saved: {formatLastSaved(lastSaved)}</span>
223          )}
224        </div>
225      </div>
226
227      <div className="grid gap-4">
228        <Card>
229          <CardHeader>
230            <CardTitle className="flex items-center gap-2">
231              <FileText className="h-5 w-5" />
232              Developer Notepad
233            </CardTitle>
234            <CardDescription>
235              Store your coding snippets, commands, and quick references in
236              Markdown format. Perfect for keeping your development notes
237              organized and accessible.
238            </CardDescription>
239          </CardHeader>
240          <CardContent className="space-y-4">
241            {/* Disclaimer */}
242            <div className="rounded-md border border-yellow-200 bg-yellow-50 p-3 text-sm">
243              <div className="flex items-start gap-2">
244                <div className="text-yellow-600"></div>
245                <div className="text-yellow-800">
246                  <strong>Important:</strong> Your notes are stored in your
247                  browser&apos;s IndexedDB and may be lost when clearing browser
248                  data. Use the export button regularly to backup your important
249                  notes.
250                </div>
251              </div>
252            </div>
253
254            {/* Action Buttons */}
255            <div className="flex flex-wrap gap-2">
256              <Button onClick={handleManualSave} variant="default" size="sm">
257                <Save className="h-4 w-4 mr-2" />
258                Save Now
259              </Button>
260              <Button onClick={handleExport} variant="outline" size="sm">
261                <Download className="h-4 w-4 mr-2" />
262                Export .md
263              </Button>
264              <Button
265                onClick={() => fileInputRef.current?.click()}
266                variant="outline"
267                size="sm"
268              >
269                <Upload className="h-4 w-4 mr-2" />
270                Import
271              </Button>
272              <Button onClick={handleCopyAll} variant="outline" size="sm">
273                Copy All
274              </Button>
275              <Button onClick={handleClear} variant="destructive" size="sm">
276                <Trash2 className="h-4 w-4 mr-2" />
277                Clear
278              </Button>
279            </div>
280
281            {/* Hidden file input for import */}
282            <input
283              ref={fileInputRef}
284              type="file"
285              accept=".md,.txt,text/markdown,text/plain"
286              onChange={handleImport}
287              className="hidden"
288            />
289
290            {/* Main textarea */}
291            <div className="space-y-2">
292              <Textarea
293                value={content}
294                onChange={(e) => setContent(e.target.value)}
295                placeholder="Add your notes and code snippets here..."
296                className="min-h-[500px] font-mono text-sm resize-y"
297              />
298            </div>
299
300            {/* Stats */}
301            <div className="flex items-center justify-between text-sm text-muted-foreground border-t pt-4">
302              <div className="flex gap-4">
303                <span>Characters: {content.length.toLocaleString()}</span>
304                <span>
305                  Words:{' '}
306                  {content.trim()
307                    ? content.trim().split(/\s+/).length.toLocaleString()
308                    : 0}
309                </span>
310                <span>
311                  Lines: {content.split('\n').length.toLocaleString()}
312                </span>
313              </div>
314              <div>Auto-save enabled</div>
315            </div>
316          </CardContent>
317        </Card>
318
319        {/* Markdown Cheat Sheet */}
320        <Card>
321          <CardHeader>
322            <CardTitle>Markdown Quick Reference</CardTitle>
323            <CardDescription>
324              Common Markdown syntax for formatting your notes
325            </CardDescription>
326          </CardHeader>
327          <CardContent>
328            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 text-sm">
329              <div>
330                <h4 className="font-semibold mb-2">Headers</h4>
